mapper.select:
SELECT COUNT(r.id) total,SUM(r.amount) amountTotal
FROM trade_records r 
WHERE r.trade_type_id = #{tradeTypeId}

     AND r.terminal_number = #{terminalNumber}  


     AND DATE_FORMAT(r.traded_at, '%Y-%m-%d') >= #{startTime} 


     AND DATE_FORMAT(r.traded_at, '%Y-%m-%d') <= #{endTime}

SELECT r.id,
DATE_FORMAT(r.traded_at, '%Y-%m-%d %T') tradedTimeStr,
r.trade_number,
r.amount,
r.poundage,
r.terminal_number terminalNumber,
r.traded_status tradedStatus
FROM trade_records r 
WHERE r.trade_type_id = #{tradeTypeId}

     AND r.terminal_number = #{terminalNumber}  


     AND DATE_FORMAT(r.traded_at, '%Y-%m-%d') >= #{startTime} 


     AND DATE_FORMAT(r.traded_at, '%Y-%m-%d') <= #{endTime} 

ORDER BY r.traded_at DESC
LIMIT #{offset}, #{rows}
