mapper.select:
SELECT count(o.id) as count FROM cs_returns o LEFT JOIN terminals t ON o.terminal_id = t.id WHERE o.customer_id = #{customer_id}
and (o.apply_num like "%"#{search}"%" or t.serial_num like "%"#{search}"%")
and o.status = #{q}
ORDER BY o.created_at DESC

SELECT o.id, o.created_at, o.status, t.serial_num, o.apply_num, gb.`name` AS brand_name, g.model_number AS brand_number, pc.`name` as zhifu_pt, m.title AS merchant_name, m.phone AS mer_phone FROM cs_returns o LEFT JOIN terminals t ON o.terminal_id = t.id LEFT JOIN goods g ON t.good_id = g.id LEFT JOIN good_brands gb ON g.good_brands_id = gb.id LEFT JOIN pay_channels pc ON t.pay_channel_id = pc.id LEFT JOIN merchants m ON t.merchant_id = m.id WHERE o.customer_id = #{customer_id}
and (o.apply_num like "%"#{search}"%" or t.serial_num like "%"#{search}"%")
and o.status = #{q}
ORDER BY o.created_at DESC LIMIT #{offset},#{pageSize}

mapper.update:
update  cs_returns 
SET status = #{repairStatus.code},updated_at = NOW()
where id = #{id}

update  cs_returns 
SET STATUS = #{updateStatus.code},updated_at = NOW()
where id = #{id}
