mapper.select:

SELECT param_name, param_value, remark
  FROM sys_config
 WHERE param_name = #{paramName}


SELECT name, phone, email, city_id, integral, password
  FROM customers
 WHERE id = #{id}

mapper.update:
UPDATE customers

	name = #{name},
	phone = #{phone},
	email = #{email},
	city_id = #{cityId}

WHERE id = #{id}

update customers
set name = #{name},phone = #{phone},email = #{email},city_id = #{cityId},updated_at = NOW()
where id = #{id}

mapper.select:

SELECT COUNT(i.id)
  FROM customer_integral_records i LEFT JOIN orders d ON i.target_id = d.id
 WHERE i.customer_id = #{customerId}


	SELECT i.quantity, i.types, i.target_type, d.order_number, d.actual_price,
		   DATE_FORMAT(d.payed_at, '%Y-%m-%d %T') payedAt
	  FROM customer_integral_records i LEFT JOIN orders d ON i.target_id = d.id
	 WHERE i.customer_id = #{customerId}
  ORDER BY i.created_at DESC
	 LIMIT #{offset}, #{rows}


SELECT SUM(i.quantity) quantityTotal
  FROM customer_integral_records i
 WHERE i.customer_id = #{customerId}


SELECT id, city_id, receiver, moblephone, zip_code, is_default, address
  FROM customer_addresses
 WHERE id = #{id}

mapper.update:
UPDATE customer_addresses
city_id = #{cityId}, zip_code = #{zipCode}, address = #{address}, receiver = #{receiver}, telphone = #{telphone}, moblephone = #{moblephone}, is_default = #{isDefault}
WHERE id = #{id}
