mapper.select:
SELECT DISTINCT g.id,g.Title,g.has_purchase,b.name,g.Model_number,g.volume_number, g.retail_price,g.total_score from goods g LEFT JOIN good_brands b on g.good_brands_id=b.id LEFT JOIN goods_pay_channels gpc on gpc.good_id=g.id LEFT JOIN pay_channels pc ON pc.id=gpc.pay_channel_id and pc.status=5 where g.status=5 and pc.id in (SELECT sa.id from support_areas sa where sa.city_id=#{city_id} and sa.support_type=1)
and g.has_purchase=1
and g.retail_price >= #{minPricei}
and g.retail_price <= #{maxPricei}
and g.good_brands_id in ${brands_ids}
and g.pos_category_id in ${categorys}
and pc.id in ${categorys}
and g.id in (SELECT gct.good_id from good_card_types gct WHERE gct.card_type_id in ${pay_card_ids})
and pc.id in(SELECT stt.pay_channel_id from support_trade_types stt where stt.trade_type in ${trade_type_ids})
and g.sign_order_way_id in ${sale_slip_ids}
and pc.id in (SELECT pcbc.pay_channel_id from pay_channel_billing_cycles pcbc where pcbc.billing_cycles_id in ${tDates})
order by g.published_at desc
order by g.volume_number desc
order by g.retail_price desc
order by g.retail_price
order by g.total_score desc
